mapper.select:
    	SELECT
    		COUNT(seq) AS totalCnt,
    		SUM(price) AS totalPrice
		FROM oms_corp_moneybook
		WHERE memberId = #{memberId}
		  AND usedDate BETWEEN #{startDate} AND #{endDate}

mapper.insert:
    	INSERT INTO `oms_corp_moneybook` (`seq`, `usedDate`, `category`, `customer`, `usedPlace`, `price`, `note`, `memberId`, `registeredDate`, `modifiedDate`)
		VALUES ( IF((SELECT MAX(seq) FROM oms_corp_moneybook t) IS NULL, 0, (SELECT MAX(seq) + 1 FROM oms_corp_moneybook t1) ), 
				 #{usedDate}, #{category}, #{customer}, #{usedPlace}, #{price}, #{note}, #{memberId}, SYSDATE(), SYSDATE());

mapper.delete:
DELETE FROM `oms_corp_moneybook`
WHERE seq = #{seq}
  AND memberId = #{memberId}
