mapper.select:
SELECT `no`, seq, sentMemberId, sentMemberName, receivedMemberId, receivedMemberName, title, statusCodeGroup, statusCode, startDate, endDate, completedDate, registeredDate FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS `no`, seq, sentMemberId, sentMemberName, receivedMemberId, receivedMemberName, title, statusCodeGroup, statusCode, startDate, endDate, completedDate, registeredDate FROM ( SELECT @ROWNUM := 0 AS `no`) R, ( SELECT PMA.seq, PMA.sentMemberId, M.memberName AS sentMemberName, PMA.receivedMemberId, M2.memberName AS receivedMemberName, PMA.title, PMA.statusCodeGroup, PMA.statusCode, PMA.startDate, PMA.endDate, DATE_FORMAT(PMA.completedDate, '%Y-%m-%d %H:%i:%s') AS completedDate, DATE_FORMAT(PMA.registeredDate, '%Y-%m-%d %H:%i:%s') AS registeredDate FROM oms_person_moneybook_approval PMA, oms_member M, oms_member M2 WHERE PMA.sentMemberId = M.memberId AND PMA.receivedMemberId = M2.memberId AND PMA.sentMemberId = #{sentMemberId}
AND PMA.statusCode = #{statusCode}
AND DATE_FORMAT(PMA.registeredDate, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate} ORDER BY PMA.${sort} ${order} ) T ) T1 WHERE `no` BETWEEN #{start} AND #{end}

    	SELECT
    		COUNT(seq) AS totalCnt
		FROM oms_person_moneybook_approval
		WHERE sentMemberId = #{sentMemberId}
		  
			  AND statusCode = #{statusCode}
		  
		  AND DATE_FORMAT(registeredDate, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}

SELECT `no`, seq, sentMemberId, sentMemberName, receivedMemberId, receivedMemberName, title, statusCodeGroup, statusCode, startDate, endDate, completedDate, registeredDate FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS `no`, seq, sentMemberId, sentMemberName, receivedMemberId, receivedMemberName, title, statusCodeGroup, statusCode, startDate, endDate, completedDate, registeredDate FROM ( SELECT @ROWNUM := 0 AS `no`) R, ( SELECT PMA.seq, PMA.sentMemberId, M.memberName AS sentMemberName, PMA.receivedMemberId, M2.memberName AS receivedMemberName, PMA.title, PMA.statusCodeGroup, PMA.statusCode, PMA.startDate, PMA.endDate, DATE_FORMAT(PMA.completedDate, '%Y-%m-%d %H:%i:%s') AS completedDate, DATE_FORMAT(PMA.registeredDate, '%Y-%m-%d %H:%i:%s') AS registeredDate FROM oms_person_moneybook_approval PMA, oms_member M, oms_member M2 WHERE PMA.sentMemberId = M.memberId AND PMA.receivedMemberId = M2.memberId AND PMA.receivedMemberId = #{receivedMemberId}
AND PMA.statusCode = #{statusCode}
AND DATE_FORMAT(PMA.registeredDate, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate} ORDER BY PMA.${sort} ${order} ) T ) T1 WHERE `no` BETWEEN #{start} AND #{end}

    	SELECT
    		COUNT(seq) AS totalCnt
		FROM oms_person_moneybook_approval
		WHERE receivedMemberId = #{receivedMemberId}
		  
			  AND statusCode = #{statusCode}
		  
		  AND DATE_FORMAT(registeredDate, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}

mapper.update:
UPDATE `oms_person_moneybook_approval`
SET completedDate = SYSDATE(),
	
		receivedMemberSign = #{vo.receivedMemberSign},
	
	statusCode = #{vo.statusCode}
WHERE seq IN 
	
		#{seq}
	
  AND receivedMemberId = #{vo.receivedMemberId}

mapper.delete:
DELETE FROM `oms_person_moneybook_approval`
WHERE seq = #{seq}
	
		AND sentMemberId = #{sentMemberId} 
	
	
		AND receivedMemberId = #{receivedMemberId}
