mapper.select:
    	SELECT
    		COUNT(seq) AS totalCnt,
    		SUM(price) AS totalPrice
		FROM oms_person_moneybook
		WHERE memberId = #{memberId}
		  AND usedDate BETWEEN #{startDate} AND #{endDate}

mapper.delete:
DELETE FROM `oms_person_moneybook`
WHERE seq = #{seq}
  AND memberId = #{memberId}
